import React from 'react';
import { Card, Col, Row } from 'antd';
import axios from 'axios';
import { DatePicker, Radio,  Menu, Dropdown, Button, Form  } from 'antd';

const { MonthPicker, RangePicker, WeekPicker } = DatePicker;
const FormItem = Form.Item;

class Takeattendace  extends React.Component{
    state = {
        size: '',
        studentNames:"",
        data:'',
        date:'',
      };

      handleSizeChange = (e) => {
        this.setState({ size: e.target.value });
        console.log("date",this.state.size)

      }

      onChange(date) {
          let calender = this;
          calender.setState({ date:date})
        //console.log(date, dateString);
      }


      componentDidMount(){
          let currentdata = this;
          let anotherData= this;
          var listData =[];
          var ID = [];
        axios.get('http://localhost:9008/student')
        .then(function (response) {
            //console.log(response.data);
            //currentdata.setState({data:response.data})
            //let apiData = response.data;

          // anotherData.state({data:apiData})
            for(let data of response.data){
                  //console.log(data.id);
                  ID.push(data.id)
                  console.log(ID)
                   listData.push(data.StudentName)
                   //console.log(listData)
                   currentdata.setState({data:listData});

            }

        })
        .catch(function (error) {
            console.log(error);
        });
        }




    render(){
        const { size } = this.state;
        console.log(this.state.date)
       // console.log("studentNames",this.state.studentNames)
        //console.log("apidata", this.state.data)
        // const { StudentName }  = this.state.data
        // console.log(StudentName)
        const menu = (
            <Menu>
              <Menu.Item>
              { this.state.data && this.state.data.map( (item,index) => {
                //console.log("item",item.StudentName)
                return <a key={index} value={item}>{ item}m</a>

              } ) }

              </Menu.Item>

            </Menu>
        );

        return(
            <div style={{ background: '#ECECEC', padding: '30px', paddingLeft:"40%" , height:"100vh" }}>
      <Row gutter={16}>

      <Col span={8}>
        <Card title="Take Attendance" bordered={false}>

        <DatePicker onChange={this.onChange} />

        <div>

        <FormItem>
        <Dropdown overlay={menu} placement="bottomCenter">
      <Button>Non SELECTED</Button>
    </Dropdown>

        </FormItem>

        </div>
        </Card>
      </Col>
    </Row>
  </div>


        );
    }
}

export default Takeattendace;
