import React from 'react';
import { Card, Col, Row } from 'antd';
import axios from 'axios';
import { DatePicker, Radio } from 'antd';

const { MonthPicker, RangePicker, WeekPicker } = DatePicker;

class Takeattendace  extends React.Component{
    state = {
        size: '',
        studentNames:"",
        data:''
      };

      handleSizeChange = (e) => {
        this.setState({ size: e.target.value });
        console.log("date",this.state.size)

      }


      componentDidMount(){
          let currentdata = this;
        axios.get('http://localhost:9008/student')
        .then(function (response) {
            console.log(response.data);
            let apiData = response.data;
            this.state({data:apiData})
            for(let data of apiData){
                   // console.log(data.StudentName);
                    currentdata.setState({studentNames:data.StudentName});

            }

        })
        .catch(function (error) {
            console.log(error);
        });
        }

    render(){
        const { size } = this.state;
        console.log("studentNames",this.state.studentNames)
        console.log("apidata", this.state.data)
        return(
            <div style={{ background: '#ECECEC', padding: '30px', paddingLeft:"40%"  }}>
    <Row gutter={16}>

      <Col span={8}>
        <Card title="Take Attendance" bordered={false}>
        <DatePicker size={size} />

        </Card>
      </Col>
    </Row>
  </div>


        );
    }
}

export default Takeattendace;
