import React from 'react';
import { Card, Col, Row } from 'antd';
import axios from 'axios';
import { DatePicker, Radio,  Menu, Dropdown, Button, Form, Select,Icon   } from 'antd';

const { MonthPicker, RangePicker, WeekPicker } = DatePicker;
const FormItem = Form.Item;
const Option = Select.Option;




class Viewattendace  extends React.Component{
  state={
    date:'',
    response:'',

  }

  onChange=(date, dateString)=>{

  let dates = date;

    this.setState({date:dateString});

  }


  handleSubmit=()=>{
    let res = this;
    axios.get(`http://localhost:9008/attendance/${this.state.date}`)
    .then(function (response) {
     // console.log(response.data);
     res.setState({ response:response.data})
     return(

       <div>

         {response.data.map((index, item)=>{
           return <labels key={index} value={item}>{item.StudentID }</labels>
         })}
       </div>
     )



    })
    .catch(function (error) {
      console.log(error);
    });
  }


    render(){
        console.log("Response Data::::s",this.state.response)
        return(
            <div style={{ background: '#ECECEC', padding: '30px', paddingLeft:"40%" , height:"100vh" }}>
      <Row gutter={16}>

      <Col span={8}>
        <Card title="View Attendance" bordered={false}>
        <FormItem>
        <DatePicker onChange={this.onChange} /> 

        </FormItem>


       <FormItem>

    {/* <Select defaultValue="NON SELECTED" style={{ width: 300 }} >
      <Option value="jack">NON SELECTED</Option>


    </Select> */}
       </FormItem>



          <FormItem style={{ paddingLeft:50}}>
                <Button type="primary" htmlType="submit" 
                className="login-form-button" style={{ width:210, paddingLeft:"-16px"}} 
                onClick={event=>this.handleSubmit(event)}



                >
                <Icon type="submit"/>
                  Submit
                </Button>
                </FormItem>
        </Card>
      </Col>
    </Row>
  </div>

        );
    }
}

export default Viewattendace;
