import React from 'react';
import { Form, Icon, Input, Button} from 'antd';
import { Row, Col } from 'antd';
import { Menu, Dropdown, message } from 'antd';
const FormItem = Form.Item;

const initialState = {
  employeename:'',
  email:'',
  password:'',
  confirmpassword:'',
  roll:''
}

class CreateEmployee extends React.Component{
  state={...initialState}


    handleButtonClick =(e)=> {
        message.info('Click on left button.');
        console.log('click left button', e);
      }

    handleMenuClick =(e)=> {
        message.info('Click on menu item.');
        console.log('click', e.target.value);
        console.log("key", this.props.key)
      }
      handleChange = (event) =>{
        const { name, value } = event.target;

        this.setState({[name]: value})
      };

      handleSubmit = () => {
        console.log('Data:::', this.state)
        this.clearState();

      }
      validateForm =()=>{
        const  { employeename,email,  password, confirmpassword,roll } = this.state;
        const isInvalid = !employeename || !email ||  password!==confirmpassword  ||!roll ;
        return isInvalid;
     }
     clearState =()=>{
      this.setState({ ...initialState });
  }


      render(){
        const  { employeename,email,  password, confirmpassword,roll } = this.state;
        const menu = (
            <Menu onClick={this.handleMenuClick} name="roll" value={roll}>
              <Menu.Item key="1"><img style={{width:20}} src={require('../image/admin.png')} alt="" />Admin</Menu.Item>
              <Menu.Item key="2"><Icon type="user" />User</Menu.Item>
              {/* <Menu.Item key="3"><Icon type="user" />3rd item</Menu.Item> */}
            </Menu>
          );
        return(
            <div >
            {/* <image src={{backgroundImage:'../image/background.jpg'}}></image> */}

              <Row>
              <Col span={12} offset={6}>

              <div className="login-form{" style={{display:"flex"}}>
              <div style={{margin:160}}>
            <Form className="login-form" style={{ width:500, backgroundColor:'white'}}> 
          <div style={{paddingTop:30}}>
            <strong><span className="login100-form-title p-b-51" style={{paddingLeft:50}}>
            <img style={{width:80}} src={require('../image/create.png')} alt="" />
                    Add Employee
                  </span></strong></div>
               <FormItem style={{paddingLeft:100, paddingTop:50}}> 
              <FormItem>

                  <Input className='input-sigin' name='employeename'
                  value={employeename}
                  onChange={event => this.handleChange(event)}
                  prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />} 
                  placeholder="Employee Name" />
              </FormItem>
              <FormItem>

                <Input className='input-sigin' name='email'
                value={email}
                onChange={event => this.handleChange(event)}
                prefix={<Icon type="mail" theme="outlined" /> }
                placeholder="Email" />
            </FormItem>
              <FormItem>

                  <Input className='input-sigin' 
                  name='password'
                  value={password}

                  onChange={event => this.handleChange(event)}
                  prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />} 
                  type="password" placeholder="Password" />

              </FormItem>
              <FormItem>

               <Input className='input-sigin' 
               name='confirmpassword'
               value={confirmpassword}
               onChange={event => this.handleChange(event)}
               prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />} 
               type="password" placeholder="Confirm Password" />

           </FormItem>
              <FormItem>

                <Dropdown.Button onClick={this.handleButtonClick} overlay={menu} >
                  Create Roll
                </Dropdown.Button>
              </FormItem>
              </FormItem>

                <FormItem style={{ paddingLeft:130, paddingBottom:30}}>
                <Button type="primary" htmlType="submit" 
                className="login-form-button" style={{ width:210, paddingLeft:50}} 
                onClick={this.handleSubmit}
                disabled={ this.validateForm()}
                >
                  Submit
                </Button>
                </FormItem>

            </Form>
            </div>
            </div>
            </Col>
            </Row>

            </div>
        );
    }
}

export default CreateEmployee;
