import React from 'react';
import { Card, Col, Row } from 'antd';
import axios from 'axios';
import { DatePicker, Radio,  Menu, Dropdown, Button, Form, Select  } from 'antd';

const { MonthPicker, RangePicker, WeekPicker } = DatePicker;
const FormItem = Form.Item;
const Option = Select.Option;

class Takeattendace  extends React.Component{
    state = {

        studentNames:"",
        data:'',
        date:'',
        dropdown:''
      };



      onChange=(dateString, date)=> {
          this.setState({ date:date})

      }

      dropDownChange=(event)=>{
          const{ name, value } = event.target;
          console.log(name.value);
      }

      componentDidMount(){
          let currentdata = this;
          let anotherData= this;
          var listData =[];
          var ID = [];
        axios.get('http://localhost:9008/student')
        .then(function (response) {
             for(let data of response.data){
                 ID.push(data.id)
                  console.log(ID)
                   listData.push(data.StudentName)
                   //console.log(listData)
                   currentdata.setState({data:listData});

            }

        })
        .catch(function (error) {
            console.log(error);
        });
        }




    render(){
        console.log("Date:::", this.state.date)
        const menu = (
            <Menu>

              { this.state.data && this.state.data.map( (item,index) => {

                return <Menu.Item  key={index} value={item}>{ item}</Menu.Item>

              } ) }



            </Menu>
        );

        return(
            <div style={{ background: '#ECECEC', padding: '30px', paddingLeft:"40%" , height:"100vh" }}>
      <Row gutter={16}>

      <Col span={8}>
        <Card title="Take Attendance" bordered={false}>

        <DatePicker onChange={this.onChange} />

        <div>      
        <Dropdown    overlay={menu} placement="bottomCenter">
      <Button name='dropdown' onChange={(event)=>this.dropDownChange(event)}>Non SELECTED</Button>
    </Dropdown>

        </div>
        <div>
    <Select defaultValue="lucy" style={{ width: 120 }} onChange={handleChange}>
      <Option value="jack">Jack</Option>
      <Option value="lucy">Lucy</Option>
      <Option value="disabled" disabled>Disabled</Option>
      <Option value="Yiminghe">yiminghe</Option>
    </Select>
    <Select defaultValue="lucy" style={{ width: 120 }} allowClear disabled>
      <Option value="lucy">Lucy</Option>
    </Select>
  </div>


        </Card>
      </Col>
    </Row>
  </div>


        );
    }
}

export default Takeattendace;
